    const dbRef = ref(getDatabase());
    get(child(dbRef, "tarp/product1")).then((snapshot) => {
      var dbdata = snapshot.val();
      setProduct1(dbdata);
    });
    get(child(dbRef, "tarp/product2")).then((snapshot) => {
      var dbdata = snapshot.val();
      setProduct2(dbdata);
    });
    get(child(dbRef, "tarp/product3")).then((snapshot) => {
      var dbdata = snapshot.val();
      setProduct3(dbdata);
    });
  }

