      option2: "뛰빠빠빠빠욜로",
      option3: "뛰빠뛰빠뒤빠빠",
      detail: "세상에서 가장 큰",
      jpg: "",
    },
    2: {
      name: "tent2",
      price: 2000,
      option1: "빨간거",
      option2: "쉬빨간거",
      option3: "핵빨간거",
      detail: "제일 이상한",
      jpg: "",
    },
    3: {
      name: "tent3",
      price: 3000,
      option1: "빨간거",
      option2: "쉬빨간거",
      option3: "핵빨간거",
      detail: "거대한",
      jpg: "",
    },
    4: {
      name: "tent4",
      price: 400000,
      option1: "빨간거",
      option2: "쉬빨간거",
      option3: "핵빨간거",
      detail: "완전 아싸",
      jpg: "",
    }
  };

  const product2 = {
    21: {
      name: "tent2-1",
      price: 1000,
      option1: "쉬파파파파욜로",
      option2: "뛰빠빠빠빠욜로",
      option3: "뛰빠뛰빠뒤빠빠",
      detail: "국내에서 제일 잘 팔리는",
      jpg: "",
