    const newProduct = { ...hi }; // toDos 객체를 ...으로 불러와서 다시 만들어 새 객체를 만듬
    delete newProduct[key]; //이 오브젝트에서 key를 삭제함
    setHi(newProduct);
  };


  //========================================== Product_info_detail (side)function =====================================
  const Product_info_detail = ({ detail, name, price }) => {
    return (
